import React from 'react';
import { datos } from './datos.json';
import './App.css';

class Work extends React.Component {
  constructor() {
    super();
    this.state = {
      datos,
    };
  }

  // Mostrar la alerta con más información
  Mialerta(datos) {
    alert(`Información adicional: ${datos.Nombre}, ${datos.Pais}, ${datos.Descripcion}`);
  }

  render() {
    return (
      <div className="box">
        {this.state.datos.map((datos, i) => (
          <div className="card" key={i}>
            <div className="card-body">
              <h2 className="card-name">{datos.Nombre}</h2>
              <p className="card-text">{datos.Pais}</p>
              <p className="card-text">{datos.Codigo}</p>
              <p className="card-text">{datos.Descripcion}</p>
              <button
                type="button"
                className="btn btn-outline-primary"
                onClick={() => this.Mialerta(datos)}
              >
                Más info
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  }
}

export default Work;
