import React from 'react';
import datos from './datos.json'; // Cambio aquí
import $ from 'jquery';
import './App.css';

class Work extends React.Component {
  constructor(){
    super();
    this.state = {
      datos
    }
  }

  Mialerta() {
    alert("Holaaaa soy tu primer alert con react!!");
  }

  render() {
    const datosRenderizados = this.state.datos.map((dato, i) => {
      return(
        <div key={i}>
          <div className="card-body">
            <h2 className="card-name"> 
              {dato.Nombre}
            </h2>
            <p className="card-text">
              {dato.Pais}
            </p>   
            <p className="card-text">
              {dato.Codigo}
            </p>
            <p className="card-text">
              {dato.Descripcion}
            </p>
            <button type="button" className="btn btn-outline-primary" onClick={this.Mialerta}>más info</button>
          </div>  
        </div>     
      )
    })

    return(
      <div className="box">
        {datosRenderizados}
      </div>
    )
  }
}

export default Work;
