import React from 'react';
import './App.css';

function Misimagenes() {
  // Puedes poner las imágenes en un array, facilitando el manejo de ellas.
  const imagenes = [
    'https://auren.com/co/wp-content/uploads/2021/10/Sector-agropecuario-2-scaled.jpg',
    'mueble-2.jpg',
    'mueble-3.jpg',
    'mueble-4.jpg',
    'mueble-5.jpg'
  ];

  return (
    <>
      <div className="image-container">
        {/* Usamos map para iterar sobre las imágenes de forma más dinámica */}
        {imagenes.map((imagen, index) => (
          <img
            key={index}
            src={require(`./imagenes/${imagen}`)} // Cargar imágenes desde la carpeta
            alt={`mueble-${index + 1}`}
            className="galeria-image"
          />
        ))}
      </div>
    </>
  );
}

class Galeria extends React.Component {
  render() {
    return (
      <div className="galeria-container">
        <h2 className="galeria-title text-center">Galería de Imágenes</h2>
        <p className="text-center">Explora nuestras imágenes y descubre más sobre nuestros productos.</p>
        <div className="galeria-images">
          <Misimagenes />
        </div>
      </div>
    );
  }
}

export default Galeria;
